INSERT INTO `wp_posts` VALUES ('25', '1', '2016-07-21 15:16:09', '2016-07-21 07:16:09', '具体操作步骤：\r\n\r\n关闭mysql:\r\n# service mysqld stop\r\n然后:\r\n# mysqld_safe --skip-grant-tables\r\n启动mysql:\r\n# service mysqld start\r\n# mysql -u root\r\nmysql&gt; use mysql\r\nmysql&gt; UPDATE user SET Password=PASSWORD(\'xxx\') WHERE user=\'root\';\r\n（如果没用执行 UPDATE user SET Password=PASSWORD(\'\') WHERE user=\'\';）\r\nmysql&gt; delete from user where user=\'\';\r\nmysql&gt; flush privileges;\r\nmysql&gt;\\q\r\n问题解决。', '解决Mysql报Access denied for user \'\'@\'localhost\' to database \'mysql\'问题', '', 'publish', 'open', 'open', '', 'mysql-%e8%a7%a3%e5%86%b3access-denied-for-user-localhost-to-database-mysql%e9%97%ae%e9%a2%98', '', '', '2016-08-18 15:13:05', '2016-08-18 07:13:05', '', '0', 'http://www.iphp.cf/?p=25', '0', 'post', '', '0');
